# テーブル操作関数
def create_table_schema(columns):
    schema = ""
    for column in columns:
        schema += f"{column[0]} {column[1]},\n"
    return schema[:-2]  # 最後のカンマと改行を削除

def create_schema(schema_name):
    query = f"CREATE SCHEMA IF NOT EXISTS {schema_name};"
    spark.sql(query)
    return True

