def truncate_table(schema_name, table_name):
    query = f"TRUNCATE TABLE {schema_name}.{table_name};"
    spark.sql(query)
    return True

def drop_table(schema_name, table_name):
    query = f"DROP TABLE {schema_name}.{table_name};"
    spark.sql(query)
    return True


